mapper.select:
select a.* from ( select @rownum:=@rownum+1 as idx, wv.no, wv.booktitle, wv.bookimg, wv.title, wv.content, wv.good goodCnt, wv.nickname, wv.regDate, wv.userimg
,(select count(*) from good g where wv.no = g.writingNo and g.userNo = #{userNo}) isGood, (select count(*) from scrap s where wv.no = s.writingNo and s.userNo = #{userNo}) isScrap
from writingview wv, (select @rownum:=0) r where (booktitle like concat('%',#{keyword},'%') or (tag like concat('%',#{keyword},'%')))
and division like #{division}
and
category like concat('%',#{category},'%')
order by regDate
order by good
) a limit ${offset}, ${perPage}
